mapper.select:
SELECT cargo_id AS cargoId, item_id AS itemId, status_id AS statusId
FROM cargo
WHERE item_id = #{itemId} AND status_id != #{statusId}

SELECT i.item_id AS itemId, i.item_name AS itemName, COUNT(*) AS cnt FROM item i LEFT JOIN cargo c ON i.item_id = c.item_id WHERE c.status_id=3
AND i.item_name LIKE #{itemName}
GROUP BY i.item_id LIMIT #{pageSize} OFFSET #{offset}

SELECT count(*) FROM( SELECT i.item_id AS itemId, i.item_name AS itemName, COUNT(*) AS cnt FROM item i LEFT JOIN cargo c ON i.item_id = c.item_id WHERE c.status_id=3
AND i.item_name LIKE #{itemName}
GROUP BY i.item_id ) t

SELECT c.cargo_id AS cargoId,i.item_name AS itemName, c.status_id AS statusId
FROM cargo c join item i on c.item_id = i.item_id

	WHERE i.item_name LIKE #{itemName}

ORDER BY c.cargo_id
LIMIT #{pageSize} OFFSET #{offset}

SELECT count(*)
FROM(
		SELECT c.cargo_id AS cargoId,i.item_name AS itemName, c.status_id AS statusId
		FROM cargo c join item i on c.item_id = i.item_id

			WHERE i.item_name LIKE #{itemName}

) t

mapper.update:
UPDATE cargo
SET status_id = #{statusId}
WHERE cargo_id = #{cargoId}
